import React, { useState, useEffect, useRef } from "react";
import "./ChatWindow.css";
import { getAIMessage } from "../api/api.js";
import { marked } from "marked";
import PartCard from "./PartCard.jsx";

function ChatWindow() {
  const defaultMessage = [{
    role: "assistant",
    content: "ðŸ‘‹ Welcome to PartSelect! I'm your appliance parts assistant, specializing in Refrigerator and Dishwasher parts.\nI can help you:\n- Find the right parts based on symptoms or part descriptions\n- Provide details on pricing, installation difficulty, and compatibility\n- Guide you to our compatibility checker where you can verify if parts work with your model\n- Share installation videos and repair guidance\n- Assist with brand-specific replacement parts\n\nWhat refrigerator or dishwasher part information can I help you with today?"
  }];

  const [isLoading, setIsLoading] = useState(false);
  const [messages, setMessages] = useState(defaultMessage);
  const [input, setInput] = useState("");

  const messagesEndRef = useRef(null);

  const scrollToBottom = () => {
    messagesEndRef.current?.scrollIntoView({ behavior: "smooth" });
  };

  useEffect(() => {
    scrollToBottom();
  }, [messages]);

  const handleSend = async (input) => {
    if (input.trim() !== "") {
      // Set user message
      setMessages(prevMessages => [...prevMessages, { role: "user", content: input }]);
      setInput("");

      setIsLoading(true);

      try {
        // Call API & set assistant message
        const response = await getAIMessage(input);

        // Check if partData is available in the response
        const newMessage = {
          role: "assistant",
          content: response.reply || response.content,
          partData: response.partData || null
        };

        setMessages(prevMessages => [...prevMessages, newMessage]);
      } catch (error) {
        console.error("Error getting AI response:", error);

        // Add error message
        setMessages(prevMessages => [
          ...prevMessages, 
          { 
            role: "assistant", 
            content: "I'm sorry, I encountered an error processing your request. Please try again." 
          }
        ]);
      } finally {
        setIsLoading(false);
      }
    }
  };

  // Function to enhance links in the message content
  const processMessageContent = (content) => {
    // Replace URLs with clickable links
    const urlRegex = /(https?:\/\/[^\s]+)/g;
    const processedContent = content.replace(urlRegex, url => {
      return `[${url}](${url})`;
    });

    return processedContent;
  };

  return (
    <div className="messages-container">
      {messages.map((message, index) => (
        <div key={index} className={`${message.role}-message-container`}>
          {message.content && (
            <div className={`message ${message.role}-message`}>
              <div 
                dangerouslySetInnerHTML={{
                  __html: marked(processMessageContent(message.content)).replace(/<p>|<\/p>/g, "")
                }}
              />

              {/* Render PartCard if partData is available */}
              {message.partData && <PartCard part={message.partData} />}
            </div>
          )}
        </div>
      ))}
      <div ref={messagesEndRef} />

      {isLoading && (
        <div className="assistant-message-container">
          <div className="message assistant-message">
            Thinking...
          </div>
        </div>
      )}

      <div className="input-area">
        <div className="input-wrapper">
          <input
            value={input}
            onChange={(e) => setInput(e.target.value)}
            placeholder="Ask me about refrigerator or dishwasher parts..."
            onKeyPress={(e) => {
              if (e.key === "Enter" && !e.shiftKey) {
                handleSend(input);
                e.preventDefault();
              }
            }}
            rows="3"
          />
          <button className="send-button" onClick={() => handleSend(input)}>
            <svg 
              xmlns="http://www.w3.org/2000/svg" 
              width="24" 
              height="24" 
              fill="none" 
              stroke="orange" 
              strokeWidth="2" 
              strokeLinecap="round" 
              strokeLinejoin="round" 
              className="feather feather-send"
            >
              <line x1="22" y1="2" x2="11" y2="13"></line>
              <polygon points="22 2 15 22 11 13 2 9 22 2"></polygon>
            </svg>
          </button>
        </div>
      </div>
    </div>
  );
}

export default ChatWindow;
