import React, { useState, useEffect, useRef } from "react";
import "./ChatWindow.css";
import { getAIMessage } from "../api/api.js";
import { marked } from "marked";
import QuickPromptButtons from "./QuickPromptButtons";

function ChatWindow() {
  const defaultMessage = [{
    role: "assistant",
    content: "Hi! I am PartSelect's Refrigerator/Dishwater chatbot!"
  }];

  const [isLoading, setIsLoading] = useState(false);
  const [messages, setMessages] = useState(defaultMessage);
  const [input, setInput] = useState("");
  const inputRef = useRef(null); // Reference to the input element

  const messagesEndRef = useRef(null);

  const scrollToBottom = () => {
    messagesEndRef.current?.scrollIntoView({ behavior: "smooth" });
  };

  useEffect(() => {
    scrollToBottom();
  }, [messages]);

  const handleSend = async (inputText) => {
    if (inputText.trim() !== "") {
      // Set user message
      setMessages(prevMessages => [...prevMessages, { role: "user", content: inputText }]);
      setInput("");

      setIsLoading(true);

      // Call API & set assistant message
      const newMessage = await getAIMessage(inputText);
      setIsLoading(false);
      setMessages(prevMessages => [...prevMessages, newMessage]);
    }
  };

  // Handle quick prompt selection
  const handleSelectPrompt = (promptTemplate) => {
    setInput(promptTemplate);
    // Focus the input after selecting a prompt
    if (inputRef.current) {
      inputRef.current.focus();

      // Place cursor at the end of the text or at the position where part number should be inserted
      const cursorPosition = promptTemplate.includes('part number') 
        ? promptTemplate.indexOf('part number') + 'part number'.length
        : promptTemplate.length;

      setTimeout(() => {
        inputRef.current.setSelectionRange(cursorPosition, cursorPosition);
      }, 0);
    }
  };

  return (
    <div className="messages-container">
      {messages.map((message, index) => (
        <div key={index} className={`${message.role}-message-container`}>
          {message.content && (
            <div className={`message ${message.role}-message`}>
              <div dangerouslySetInnerHTML={{__html: marked(message.content).replace(/<p>|<\/p>/g, "")}}></div>
            </div>
          )}
        </div>
      ))}
      <div ref={messagesEndRef} />
      {isLoading && (
        <div className="assistant-message-container">
          <div className="message assistant-message">
            Thinking...
          </div>
        </div>
      )}
      <div className="input-area">
        <div className="input-container">
          {/* Add Quick Prompt Buttons above the input */}
          <QuickPromptButtons onSelectPrompt={handleSelectPrompt} />

          <div className="input-wrapper">
            <input
              ref={inputRef}
              value={input}
              onChange={(e) => setInput(e.target.value)}
              placeholder="Ask me about refrigerator or dishwasher parts..."
              onKeyPress={(e) => {
                if (e.key === "Enter" && !e.shiftKey) {
                  handleSend(input);
                  e.preventDefault();
                }
              }}
              rows="3"
            />
            <button className="send-button" onClick={() => handleSend(input)}>
              <svg 
                xmlns="http://www.w3.org/2000/svg" 
                width="24" 
                height="24" 
                fill="none" 
                stroke="orange" 
                strokeWidth="2" 
                strokeLinecap="round" 
                strokeLinejoin="round" 
                className="feather feather-send"
              >
                <line x1="22" y1="2" x2="11" y2="13"></line>
                <polygon points="22 2 15 22 11 13 2 9 22 2"></polygon>
              </svg>
            </button>
          </div>
        </div>
      </div>
    </div>
  );
}

export default ChatWindow;
